function handleDTEAChange() {
    let dt = Number(global.get('sim_departure_time'));
    let ea = Number(global.get('sim_eamount'));

    node.warn(dt)
    node.warn(ea)

    node.warn(flow.get('sim_commands_start'))
    node.warn(flow.get('sim_commands_resume'))

    let start = flow.get('sim_commands_start').split(';')
    let resume = flow.get('sim_commands_resume').split(';')

    for (let i = 0; i < start.length; i++) {
        if (start[i].includes("iso_start_v2g")) {
            let cmd = start[i].split(",")

            if (dt > 0){
                node.warn("setting dt")
                cmd[1] = dt;
            } 
            else {
                cmd[1] = -1;
            }
            if (ea > 0){
                node.warn("setting ea")
                cmd[2] = ea;
            }
            else {
                cmd[2] = -1;
            }
            start[i] = cmd.join(",");
        }
    }

    for (let i = 0; i < resume.length; i++) {
        if (resume[i].includes("iso_start_v2g")) {
            let cmd = resume[i].split(",")
            if (dt > 0){
                node.warn("setting dt")
                cmd[1] = dt;
            } 
            else {
                cmd[1] = -1;
            }
            if (ea > 0){
                node.warn("setting ea")
                cmd[2] = ea;
            }
            else {
                cmd[2] = -1;
            }
            resume[i] = cmd.join(",");
        }
    }

    start = start.join(";")
    resume = resume.join(";")

    flow.set('sim_commands_start', start);
    flow.set('sim_commands_resume', resume);

}


