    everest_message = {
        "payload": {
            "origin": msg.payload.data.origin,
            "name": msg.payload.name,
            "type": msg.payload.type,
            "args": "N/A"
        }
    };
} else if (msg.payload.type === "call") {
    everest_message = {
        "payload": {
            "origin": msg.payload.data.origin,
            "name": msg.payload.name,
            "type": msg.payload.type,
            "args": JSON.stringify(msg.payload.data.args)
        }
    };
} else if (
    ("data" in msg.payload)
    && (typeof msg.payload.data === 'object')
    && !Array.isArray(msg.payload.data)
    && (msg.payload.data !== null)
    && ("V2G_Message_ID" in msg.payload.data)
) {
    everest_message = {
        "payload": {
            "origin": "V2G_Messages",
            "name": msg.payload.data.V2G_Message_ID,
            "type": "N/A",
            "args": msg.payload.data.V2G_Message_EXI_Hex
        }
    };
    iso15118_message_ids.push(msg.payload.data.V2G_Message_ID);
} else {
    everest_message = {"payload": msg.payload};
}
everest_messages.push(everest_message);

flow.set("everest_messages", everest_messages, "memoryOnly");
flow.set("iso15118_message_ids", iso15118_message_ids, "memoryOnly");

