// Determine the scaling factor
// determine the increment over the minimum tariff
// apply the scaling factor
// more tariff = lower usage, so subtract from the max current

node.warn(msg.payload);
const original_value_objs = msg.payload.ValueInformation;
const original_values = original_value_objs.map((entry) => {
    return entry.value;
})
const min_original = Math.min(...original_values);
const max_original = Math.max(...original_values);
const scaling_factor = 16 / (max_original - min_original);
node.warn("min_original = "+min_original+" max original "+max_original+" scaling_factor" + scaling_factor);
const new_value_objs = original_value_objs.map((entry) => {
    entry.value = 16 - ((entry.value - min_original) * scaling_factor);
    entry.Unit = 'A';
    return entry;
});
msg.payload.ValueInformation = new_value_objs;
return msg;

