function handleDTEAChange() {
    let dt = global.get('sim_departure_time')
    let ea = global.get('sim_eamount')
    let cmd = `iso_set_departure_time ${dt} ${ea}`

    let start = flow.get('sim_commands_start').split(';')
    let resume = flow.get('sim_commands_resume').split(';')

    start.splice(2, 1, cmd)
    resume.splice(2, 1, cmd)
    start = start.join(';')
    resume = resume.join(';')

    flow.set('sim_commands_start', start);
    flow.set('sim_commands_resume', resume);

}


if (msg.topic.indexOf('sim_commands') > -1) {
    const s = msg.payload.split('#');
    flow.set('sim_commands_start', s[0]);
    flow.set('sim_commands_stop', s[1]);
    flow.set('sim_commands_pause', s[2]);
    flow.set('sim_commands_resume', s[3]);
    handleDTEAChange();
} else if (msg.topic === 'eamount' || msg.topic === 'departure_time') {
    handleDTEAChange();
} else if (msg.payload == 'start') {
    msg.payload = flow.get('sim_commands_start');
    return msg;
} else if (msg.payload == 'stop') {
    msg.payload = flow.get('sim_commands_stop');
    return msg;
} else if (msg.payload == 'pause') {
    msg.payload = flow.get('sim_commands_pause');
    return msg;
} else if (msg.payload == 'resume') {
    msg.payload = flow.get('sim_commands_resume');
    return msg;
} else {
    msg.payload = 'NONE';
    return msg;
}

if (msg.payload == '') {
    if (msg.topic == 'eamount') {
        global.set('sim_eamount', 60);
        return msg;
    }
    // topic == 'departure_time'
    global.set('sim_departure_time', 0);
    return msg;
}
if (msg.topic == 'eamount') {
    global.set('sim_eamount', msg.payload);
    return msg;
}

global.set('sim_departure_time', msg.payload);
return msg;

