const MAX_MESSAGES = 100;

function getLatestOrCreateArray(name, store, maxLength) {
    let value = flow.get(name, store);
    if (value === undefined) {
        value = [];
    } else if (value.length >= maxLength) {
        value = value.slice(1 - maxLength);
    }
    return value
}

var everest_messages = getLatestOrCreateArray(
    "everest_messages", "memoryOnly", MAX_MESSAGES
);
var iso15118_message_ids = getLatestOrCreateArray(
    "iso15118_message_ids", "memoryOnly", MAX_MESSAGES
);

var everest_message;
if (msg.payload.type === "result") {
    everest_message = {
        "payload": {
            "origin": msg.payload.data.origin,
            "name": msg.payload.name,
            "type": msg.payload.type,
            "args": "N/A"
        }
    };
} else if (msg.payload.type === "call") {
    everest_message = {
        "payload": {
            "origin": msg.payload.data.origin,
            "name": msg.payload.name,
            "type": msg.payload.type,
            "args": JSON.stringify(msg.payload.data.args)
        }
    };
} else if (
    ("data" in msg.payload)
    && (typeof msg.payload.data === 'object')
    && !Array.isArray(msg.payload.data)
    && (msg.payload.data !== null)
    && ("V2G_Message_ID" in msg.payload.data)
) {
    everest_message = {
        "payload": {
            "origin": "V2G_Messages",
            "name": msg.payload.data.V2G_Message_ID,
            "type": "N/A",
            "args": msg.payload.data.V2G_Message_EXI_Hex
        }
    };
    iso15118_message_ids.push(msg.payload.data.V2G_Message_ID);
} else {
    everest_message = {"payload": msg.payload};
}
everest_messages.push(everest_message);

flow.set("everest_messages", everest_messages, "memoryOnly");
flow.set("iso15118_message_ids", iso15118_message_ids, "memoryOnly");

msg.payload = everest_messages;
msg.v2g_messages = iso15118_message_ids
return msg;

flow.set('everest_messages', undefined, 'memoryOnly');
flow.set('iso15118_message_ids', undefined, 'memoryOnly');

return msg

const dt = global.get('sim_departure_time')
const eamount = global.get('sim_eamount')
const ks = 1
// The path to environment should match the path defined in `/everest-demo/nodered/Dockerfile`
const path_to_environment = '/usr/src/node-red/python_environments/everest/bin/'

msg.payload = `${path_to_environment}python3 /bin/scripts/preview_curve_4_nodered.py ${dt} ${eamount} ${ks}`
msg.topic = 'power_curve_preview'
return msg;

if (msg.topic === 'everest_external/nodered/{}/evcc/active_powercurve' ) {
    // Python auto-formats dicts to use single quotes, but JSON.parse doesn't
    // like that.  The RegEx below fixes this
    msg.payload = msg.payload.replace(/'/g, '"')
}

const data_points = JSON.parse(msg.payload)
msg.payload = [data_points]

return msg;

if(msg.topic === 'power_curve_preview') {
    // Only allow preview when not charging
    if (flow.get('charging_status') === 'Idle' ) {
        return msg
    }
}
else { // If active curve is sent...
    return msg;
}

let draw_curve = {
    "series": [msg.payload[0].series[1]],
    "data": [msg.payload[0].data[1]],
    "labels": [msg.payload[0].labels[1]]
}

let time_scale = 1
let scale = 1000
if (msg.topic === 'power_curve_preview') {
    scale = 1000
    time_scale = 1000
    draw_curve.data[0] = draw_curve.data[0].map(item => {
    return {
        x: item.x * time_scale, // Chart expects miliseconds, convert
        y: item.y * scale // Convert miliwatt to watt
    };
});
}
else {
    draw_curve.data[0] = draw_curve.data[0].map(item => {
    // Patch 4 scale, need to fix in manager later
    return {
        x: item.y * time_scale, 
        y: item.x * scale // Convert miliwatt to watt
    };
});
}

msg.payload = [draw_curve]

return msg;

progress_curve = {
    "series": [msg.payload[0].series[0]],
    "data": [msg.payload[0].data[0]],
    "labels": [msg.payload[0].labels[0]]
}
let time_scale = 1
let scale = 1
if (msg.topic === 'power_curve_preview') {
    time_scale = 1000
    scale = 1
}

progress_curve.data[0] = progress_curve.data[0].map(item => {
    return {
        x: item.y * time_scale, // X & Y need swapped
        y: item.x / scale // eamount progress
    };
});

msg.payload = [progress_curve]

return msg;

if (msg.payload === "Idle") {
    return msg;
